$(document).ready(function(){setInterval(function(){var c=document.URL.split("//");c=(c[1]?c[1]:c[0]).split("/");var b="";if(c.length>2){b=c[2]}var a=$("#item_lastadded li:first").attr("id");$.ajax({url:"http://"+c[0]+"/Event/Refresh",data:{first_id:a,type:b},success:function(f){for(var g=0;g<f.length;g++){var h=f[g].StartDate.replace(/\/Date\((-?\d+)\)\//,"$1");var e=new Date(parseInt(h));var j="<li><p class='quick_title'><a href='/Event/Detail/"+f[g].Id+"'>"+f[g].Titre+"</a></p><p class='quick_infos'>A "+f[g].Ville+", le "+dateFormat(e,"dd/mm/yyyy HH:MM:ss")+"</p></li>";$("#item_lastadded").prepend(j)}},dataType:"json"})},20000);$(window).scroll(function(){if($(window).scrollTop()==$(document).height()-$(window).height()){var c=document.URL.split("//");c=(c[1]?c[1]:c[0]).split("/");var b="";if(c.length>2){b=c[2]}var a=$(".item:last").attr("id");$.ajax({url:"http://"+c[0]+"/Event/GetNextEvents",data:{last_id:a,type:b},success:function(f){for(var g=0;g<f.length;g++){var j="<li class='item' id='"+f[g].Id+"'><a href='/Event/Detail/"+f[g].Id+"'>";if(f[g].Image!=null){j+="<img src='/'"+f[g].Image+" width='125' height='160' alt='flyers' class='flyers float-left' />"}else{j+="<img src='/Content/Images/no_avatar.gif' width='125' height='160' alt='flyers' class='flyers float-left' />"}var h=f[g].StartDate.replace(/\/Date\((-?\d+)\)\//,"$1");var e=new Date(parseInt(h));j+="<strong class='item_titre'>"+f[g].Titre+"</strong><span class='item_type float-left'>"+f[g].Type+"</span><span class='item_Date float-right'>"+dateFormat(e,"dd/mm/yyyy")+" à "+f[g].Ville+" ("+f[g].CP+")</span><span class='item_salle'>"+f[g].Salle+"</span><em class='item_descr'>"+f[g].Description+"</em></a></li>";$("#items").append(j)}},dataType:"json"})}})});